import Card from 'react-bootstrap/Card';
import './ListCards.css'
import { useEffect, useState } from "react"
// import { Link } from "react-router-dom";
import Modal from 'react-bootstrap/Modal';
import Button from 'react-bootstrap/Button';
const ListCards = () => {


    const [countries, setCountries] = useState([])
    useEffect(() => {
        fetch(' https://ih-countries-api.herokuapp.com/countries')
            .then(res => res.json())
            .then(countries => setCountries(countries))

    }, [])

    const [currentPage, setCurrentPage] = useState(1)
    const itemsPerPage = 10
    const pageNumbers = []

    const lastIndex = currentPage * itemsPerPage
    const firstIndex = lastIndex - itemsPerPage
    const currentCountries = countries.slice(firstIndex, lastIndex)

    const handlePageChange = (page) => {
        setCurrentPage(page)
    }

    const handleNextPage = () => {
        setCurrentPage(currentPage + 1)
    }

    const handlePrevPage = () => {
        setCurrentPage(currentPage - 1)
    }



    // Details Modal

    const [show, setShow] = useState(false);
    const [owner, setOwner] = useState(null);
    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);
    // function handleShow(owner) {
    //     setOwner(owner)
    //     setShow(true);
    // }

    // const handleClose = () => {
    //     setShow(false);
    //     setOwner(null);
    // }




    for (let i = 1; i <= Math.ceil(countries.length / itemsPerPage); i++) {
        pageNumbers.push(i);
    }

    return (
        <>


            {countries.length > 0 && (
                <>

                    {currentCountries.map((country) => {

                        return (
                            <>
                                <Card style={{ width: '15rem' }} key={country.alpha3Code} data-example={country} onClick={() => handleShow} >
                                    <Card.Img variant="top" src={`https://flagpedia.net/data/flags/icon/72x54/${country.alpha2Code.toLowerCase()}.png`} />
                                    <Card.Body>
                                        <Card.Title>{country.name.official}</Card.Title>
                                        <Card.Text>
                                            <p>Capital:{country.capital}</p>
                                            <p>Area:{country.area} </p>
                                            <p>Region:{country.region}</p>
                                        </Card.Text>
                                    </Card.Body>
                                </Card>

                                {/* <Modal show={show} onHide={handleClose} animation={false}>
                                    <Modal.Header closeButton>
                                        <Modal.Title>{owner.name.official}</Modal.Title>
                                    </Modal.Header>
                                    <Modal.Body>
                                        <p>Capital:{owner.capital}</p>
                                        <p>Area:{owner.area} </p>
                                        <p>Region:{owner.region}</p>

                                    </Modal.Body>
                                    <Modal.Footer>
                                    </Modal.Footer>
                                </Modal> */}
                            </>
                        );
                    })}
                </>
            )}

            <div className="pageBtn">
                <button onClick={handlePrevPage} disabled={currentPage === 1}>
                    Previous
                </button>
                <div>
                    {pageNumbers.map(number => (
                        <button key={number} onClick={() => handlePageChange(number)} disabled={number === currentPage}>
                            {number}
                        </button>
                    ))}
                </div>
                <button onClick={handleNextPage} disabled={lastIndex >= countries.length}>
                    Next
                </button>
            </div>


        </>
    )
}

export default ListCards
