import React, { useEffect, useState } from "react"
import { Link } from "react-router-dom";

const ListCards = () => {


    const [countries, setCountries] = useState([])
    useEffect(() => {
        fetch(' https://ih-countries-api.herokuapp.com/countries')
            .then(res => res.json())
            .then(countries => setCountries(countries))

    }, [])

    const [currentPage, setCurrentPage] = useState(1)
    const itemsPerPage = 10
    const pageNumbers = []

    const lastIndex = currentPage * itemsPerPage
    const firstIndex = lastIndex - itemsPerPage
    const currentCountries = countries.slice(firstIndex, lastIndex)

    const handlePageChange = (page) => {
        setCurrentPage(page)
    }

    const handleNextPage = () => {
        setCurrentPage(currentPage + 1)
    }

    const handlePrevPage = () => {
        setCurrentPage(currentPage - 1)
    }

    for (let i = 1; i <= Math.ceil(countries.length / itemsPerPage); i++) {
        pageNumbers.push(i);
    }


    return (
        <div>


            {countries.length > 0 && (
                <>

                    {currentCountries.map((country) => {

                        return (
                            <Card style={{ width: '15rem' }} key={country.alpha3Code}>
                                <Card.Img variant="top" src={`https://flagpedia.net/data/flags/icon/72x54/${country.alpha2Code.toLowerCase()}.png`} />
                                <Card.Body>
                                    <Card.Title>{country.name.official}</Card.Title>
                                    <Card.Text>
                                        <p>Capital:{country.capital}</p>
                                        <p>Area:{country.area} </p>
                                        <p>Region:{country.region}</p>
                                    </Card.Text>
                                </Card.Body>
                            </Card>
                        );
                    })}
                </>
            )}

            <div>
                <button onClick={handlePrevPage} disabled={currentPage === 1}>
                    Previous
                </button>
                <button onClick={handleNextPage} disabled={lastIndex >= countries.length}>
                    Next
                </button>
            </div>
            <div>
                {pageNumbers.map(number => (
                    <button key={number} onClick={() => handlePageChange(number)} disabled={number === currentPage}>
                        {number}
                    </button>
                ))}
            </div>
        </div>
    )
}

export default ListCards
