import Card from 'react-bootstrap/Card';
import './ListCards.css'
import { useEffect, useState } from "react"
import { Link } from "react-router-dom";
import Modal from 'react-bootstrap/Modal';
const ListCards = () => {


    const [countries, setCountries] = useState([])
    useEffect(() => {
        fetch(' https://ih-countries-api.herokuapp.com/countries')
            .then(res => res.json())
            .then(countries => setCountries(countries))

    }, [])

    const [currentPage, setCurrentPage] = useState(1)
    const itemsPerPage = 10
    const pageNumbers = []

    const lastIndex = currentPage * itemsPerPage
    const firstIndex = lastIndex - itemsPerPage
    const currentCountries = countries.slice(firstIndex, lastIndex)

    const handlePageChange = (page) => {
        setCurrentPage(page)
    }

    const handleNextPage = () => {
        setCurrentPage(currentPage + 1)
    }

    const handlePrevPage = () => {
        setCurrentPage(currentPage - 1)
    }



    // Details Modal

    const [show, setShow] = useState(false);

    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);
    for (let i = 1; i <= Math.ceil(countries.length / itemsPerPage); i++) {
        pageNumbers.push(i);
    }

    return (
        <>


            {countries.length > 0 && (
                <>

                    {currentCountries.map((country) => {

                        return (
                            <Card style={{ width: '15rem' }} key={country.alpha3Code} onClick={handleShow}>
                                <Card.Img variant="top" src={`https://flagpedia.net/data/flags/icon/72x54/${country.alpha2Code.toLowerCase()}.png`} />
                                <Card.Body>
                                    <Card.Title>{country.name.official}</Card.Title>
                                    <Card.Text>
                                        <p>Capital:{country.capital}</p>
                                        <p>Area:{country.area} </p>
                                        <p>Region:{country.region}</p>
                                    </Card.Text>
                                </Card.Body>
                            </Card>
                        );
                    })}
                </>
            )}

            <div className="pageBtn">
                <button onClick={handlePrevPage} disabled={currentPage === 1}>
                    Previous
                </button>
                <div>
                    {pageNumbers.map(number => (
                        <button key={number} onClick={() => handlePageChange(number)} disabled={number === currentPage}>
                            {number}
                        </button>
                    ))}
                </div>
                <button onClick={handleNextPage} disabled={lastIndex >= countries.length}>
                    Next
                </button>
            </div>



            <Modal show={show} onHide={handleClose} animation={false}>
                <Modal.Header closeButton>
                    <Modal.Title>Modal heading</Modal.Title>
                </Modal.Header>
                <Modal.Body>Woohoo, you are reading this text in a modal!</Modal.Body>
                <Modal.Footer>
                    <Button variant="secondary" onClick={handleClose}>
                        Close
                    </Button>
                    <Button variant="primary" onClick={handleClose}>
                        Save Changes
                    </Button>
                </Modal.Footer>
            </Modal>
        </>
    )
}

export default ListCards
